import React, { useState } from 'react';
import { FaChevronRight, FaChevronDown, FaEdit, FaTrash, FaPlus, FaExchangeAlt } from 'react-icons/fa';
import '../styles/TodoItem.css';

function TodoItem({ 
  item, 
  level, 
  onDelete, 
  onToggleComplete, 
  onUpdate, 
  onAddSubItem,
  onMove,
  otherLists
}) {
  const [isExpanded, setIsExpanded] = useState(true);
  const [isEditing, setIsEditing] = useState(false);
  const [editText, setEditText] = useState(item.text);
  const [isAddingSubItem, setIsAddingSubItem] = useState(false);
  const [newSubItemText, setNewSubItemText] = useState('');
  const [showMoveOptions, setShowMoveOptions] = useState(false);

  const hasChildren = item.children && item.children.length > 0;
  const maxLevel = 2; // 0-based index, so this is level 3

  const handleToggleExpand = () => {
    setIsExpanded(!isExpanded);
  };

  const handleEditSubmit = (e) => {
    e.preventDefault();
    if (editText.trim()) {
      onUpdate(item.id, editText);
      setIsEditing(false);
    }
  };

  const handleAddSubItemSubmit = (e) => {
    e.preventDefault();
    if (newSubItemText.trim()) {
      onAddSubItem(item.id, newSubItemText);
      setNewSubItemText('');
      setIsAddingSubItem(false);
      setIsExpanded(true); // Expand to show the new sub-item
    }
  };

  const handleMoveToList = (listId) => {
    onMove(item.id, listId);
    setShowMoveOptions(false);
  };

  const handleToggleComplete = () => {
    // Toggle the current item
    onToggleComplete(item.id, !item.is_complete);

    // Recursively toggle all children if they exist
    if (hasChildren) {
      const toggleChildren = (children) => {
        children.forEach(child => {
          onToggleComplete(child.id, !item.is_complete);
          if (child.children && child.children.length > 0) {
            toggleChildren(child.children);
          }
        });
      };
      toggleChildren(item.children);
    }
  };

  return (
    <li className={`todo-item level-${level}`}>
      <div className="item-content">
        <div className="item-main">
          {hasChildren && (
            <button 
              className="expand-button" 
              onClick={handleToggleExpand}
              aria-label={isExpanded ? "Collapse" : "Expand"}
            >
              {isExpanded ? <FaChevronDown /> : <FaChevronRight />}
            </button>
          )}

          {!hasChildren && <div className="expand-placeholder"></div>}

          <input
            type="checkbox"
            checked={item.is_complete}
            onChange={handleToggleComplete}
            className="item-checkbox"
          />

          {isEditing ? (
            <form onSubmit={handleEditSubmit} className="edit-form">
              <input
                type="text"
                value={editText}
                onChange={(e) => setEditText(e.target.value)}
                autoFocus
                className="edit-input"
              />
              <div className="edit-actions">
                <button type="submit" className="save-button">Save</button>
                <button 
                  type="button" 
                  onClick={() => {
                    setIsEditing(false);
                    setEditText(item.text);
                  }}
                  className="cancel-button"
                >
                  Cancel
                </button>
              </div>
            </form>
          ) : (
            <span className={`item-text ${item.is_complete ? 'completed' : ''}`}>
              {item.text}
            </span>
          )}
        </div>

        {!isEditing && (
          <div className="item-actions">
            {level === 0 && otherLists.length > 0 && (
              <div className="move-dropdown">
                <button 
                  className="action-button move-button"
                  onClick={() => setShowMoveOptions(!showMoveOptions)}
                  aria-label="Move to another list"
                >
                  <FaExchangeAlt />
                </button>

                {showMoveOptions && (
                  <div className="move-options">
                    {otherLists.map(list => (
                      <button 
                        key={list.id}
                        onClick={() => handleMoveToList(list.id)}
                        className="move-option"
                      >
                        {list.name}
                      </button>
                    ))}
                  </div>
                )}
              </div>
            )}

            <button 
              className="action-button edit-button"
              onClick={() => setIsEditing(true)}
              aria-label="Edit"
            >
              <FaEdit />
            </button>

            <button 
              className="action-button delete-button"
              onClick={() => onDelete(item.id)}
              aria-label="Delete"
            >
              <FaTrash />
            </button>

            {level < maxLevel && (
              <button 
                className="action-button add-sub-button"
                onClick={() => {
                  setIsAddingSubItem(!isAddingSubItem);
                  if (!isAddingSubItem) {
                    setIsExpanded(true);
                  }
                }}
                aria-label="Add sub-item"
              >
                <FaPlus />
              </button>
            )}
          </div>
        )}
      </div>

      {isAddingSubItem && (
        <form onSubmit={handleAddSubItemSubmit} className="add-sub-item-form">
          <input
            type="text"
            value={newSubItemText}
            onChange={(e) => setNewSubItemText(e.target.value)}
            placeholder="Add a sub-task..."
            autoFocus
            className="add-sub-item-input"
          />
          <div className="add-sub-item-actions">
            <button type="submit" className="save-button">Add</button>
            <button 
              type="button" 
              onClick={() => {
                setIsAddingSubItem(false);
                setNewSubItemText('');
              }}
              className="cancel-button"
            >
              Cancel
            </button>
          </div>
        </form>
      )}

      {hasChildren && isExpanded && (
        <ul className="sub-items">
          {item.children.map(child => (
            <TodoItem
              key={child.id}
              item={child}
              level={level + 1}
              onDelete={onDelete}
              onToggleComplete={onToggleComplete}
              onUpdate={onUpdate}
              onAddSubItem={onAddSubItem}
              onMove={onMove}
              otherLists={otherLists}
            />
          ))}
        </ul>
      )}
    </li>
  );
}

export default TodoItem;
